INSERT INTO `[prefix]_page_sections` (`id`, `site_key`, `page_id`, `title`, `_order`, `style`, `format`, `content`, `nl2br`, `img_thumb`, `img_thumb_path`, `img_large`, `img_large_path`, `img_anchor`, `img_link`) VALUES (1, 'default', 1, '', 1, 'title', '', 'Welcome!', 0, '', '', '', '', '', '');
INSERT INTO `[prefix]_page_sections` (`id`, `site_key`, `page_id`, `title`, `_order`, `style`, `format`, `content`, `nl2br`, `img_thumb`, `img_thumb_path`, `img_large`, `img_large_path`, `img_anchor`, `img_link`) VALUES (2, 'default', 1, '', 2, 'normal', '', 'This website was created in about 10 minutes using EasySite. With the simple content-management tools provided with EasySite, you can maintain every aspect of your website maintenance without any programming at all. EasySite is similar to website builders like GeoCities and AOL''s site builder.', 0, '', '', '', '', '', '');
INSERT INTO `[prefix]_page_sections` (`id`, `site_key`, `page_id`, `title`, `_order`, `style`, `format`, `content`, `nl2br`, `img_thumb`, `img_thumb_path`, `img_large`, `img_large_path`, `img_anchor`, `img_link`) VALUES (3, 'default', 1, '', 3, 'subtitle', '', 'Getting Started', 0, '', '', '', '', '', '');
INSERT INTO `[prefix]_page_sections` (`id`, `site_key`, `page_id`, `title`, `_order`, `style`, `format`, `content`, `nl2br`, `img_thumb`, `img_thumb_path`, `img_large`, `img_large_path`, `img_anchor`, `img_link`) VALUES (4, 'default', 1, '', 4, 'normal', '', 'The first thing that you should do is login to your new content-management tools. Please use the username and password combination that you specified during installation.\r\n\r\n<a href={internal resource_type=''link'' resource_id=''admin''}>Click here to login, and start modifying the content of your new website!</a>', 0, '', '', '', '', '', '');
INSERT INTO `[prefix]_page_sections` (`id`, `site_key`, `page_id`, `title`, `_order`, `style`, `format`, `content`, `nl2br`, `img_thumb`, `img_thumb_path`, `img_large`, `img_large_path`, `img_anchor`, `img_link`) VALUES (5, 'default', 1, '', 5, 'normal', '', 'One of the most common questions that new users have is: How do I change the blue bars at the top and left of my page? These are known as Color Bars, and are a very common design paradigm with websites. They can be changed or removed by using the Edit Settings tool after login. The horizontal/top color bar is 1 pixel wide by X pixels high, and the vertical/left color bar is 1 pixel high by Y pixels wide. The image at the upper left corner of the screen is typically used to connect the color bars at the edge (the give a more rounded effect). Hint: This is also a great place to put your company logo!<br /><br />The "ACME Widgets" title in the sample is actually a floating layer, not an image. You can edit or remove it by using the Edit Layers tool after login. If you want to achieve exact-positioning of your company/organization logo, or if you anticipate that the information in the logo will change often, you may wish to put it in a floating layer.', 0, , 'layout.gif', '', '', '', '');
